sidebarCSS:
#root.root {--toolbar-bg: #242424;}
#root.root {--frame-bg: #242424;}
#urlbar, .urlbar-input-container, .urlbarView {
  position: relative;

}

.PinnedTabsBar {
  display: flex;
  justify-content: center;
  margin: 5px 0px;
}

.PinnedTabsBar img {
  display: flex;
  justify-content: center !important;;
  margin: 5vw 2vh;
}

.tab-wrapper { 
  width: 45vw !important;
  height: 4vh;
  background-color: #303030;
  border-radius: 12px;
}

.fav-icon {
  border-radius: 3px; 
  position: fixed;
  justify-content: center !important;;
}

#urlbar, #searchbar {
  min-width: 1px;

  min-height: var(--urlbar-min-height);

  text-shadow: none;

  color: var(--toolbar-field-color);

}
#urlbar {
  display: flex;

  flex-direction: row;

  flex: 1;

  box-sizing: border-box;

  width: initial;

  height: initial;

  inset: auto;

  border: none;

  padding: initial;

  overflow: initial;

  color: initial;

  background-color: initial;

}
#urlbar {
  --urlbar-box-bgcolor: var(--toolbar-field-focus-background-color);

  --urlbar-box-focus-bgcolor: var(--button-background-color);

  --urlbar-box-hover-bgcolor: var(--button-background-color-hover);

}
#urlbar {
}
:popover-open {
  -moz-top-layer: top;

}
[popover] {
  position: fixed;

  inset: 0;

  width: fit-content;

  height: fit-content;

  margin: auto;

  border: solid;

  padding: 0.25em;

  overflow: auto;

  color: CanvasText;

  background-color: Canvas;

}
article, aside, details, div, dt, figcaption, footer, form, header, hgroup, html, main, nav, search, section, summary {
  display: block;

}
address, article, aside, blockquote, body, caption, center, col, colgroup, dd, dir, div, dl, dt, fieldset, figcaption, figure, footer, form, h1, h2, h3, h4, h5, h6, header, hgroup, hr, html, legend, li, listing, main, marquee, menu, nav, noframes, ol, p, plaintext, pre, search, section, summary, table, tbody, td, tfoot, th, thead, tr, ul, xmp {
  unicode-bidi: isolate;

}
element {
  --urlbar-container-height: 40.00px;

}
:root[lwtheme] {
